let itemList = new Array();
$( document ).on( "change","#inventur_check_input",function() {
	let found = false;
	$.each(itemList, function(index, item) {
		if(item.invnr == $('#inventur_check_input').val())
		{
			item.zuordnen=null;  //found 
			$('.table_inventur #'+$('#inventur_check_input').val()).parents().eq(1).hide();
			$('#inventur_check_input').val('');
      $( "#inventur_check_input" ).focus({preventScroll: true});
			found = true;
		}
	});
	if (found == false){
		$.ajax({
			type: "get",
